import { CommonModule, NgFor, NgIf } from '@angular/common';
import { CUSTOM_ELEMENTS_SCHEMA, Component, OnInit } from '@angular/core';
import { RouterLink } from '@angular/router';
import { IonicModule } from '@ionic/angular';
import { register } from 'swiper/element/bundle';

register()

@Component({
  selector: 'app-walkthrough',
  templateUrl: './walkthrough.component.html',
  styleUrls: ['./walkthrough.component.scss'],
  standalone: true,
  imports: [
    IonicModule,
    RouterLink,
    NgIf,
    NgFor
  ],
  schemas: [
    CUSTOM_ELEMENTS_SCHEMA,
  ]
})
export class WalkthroughComponent  implements OnInit {

  slideData: {header: string; synopsis: string; imageUrl: string; highlight: string;}[] = [
    {
      header: 'Taking you to the Cloud',
      synopsis: 'We have no change wahalas and want you to experience this joy too',
      imageUrl: 'assets/icon/walkthrough_one.svg',
      highlight: 'Secure your ride with the click of a button'
    },
    {
      header: 'You Tekisi with us, You Win!',
      synopsis: 'You don’t need to find out at Bonduma about traffic, we pass around it!',
      imageUrl: 'assets/icon/walkthrough_two.svg',
      highlight: 'When others persevere through traffic, you go around it',
    },
    {
      header: 'We know where TaxiMan Is',
      synopsis: 'You want to book a ride, we’ll find the closest taxis to you. You just have to do... nothing',
      imageUrl: 'assets/icon/walkthrough_three.svg',
      highlight: 'Where are all the taxis at the moment? Well no need for imagination. Just click'
    }
  ]

  constructor() { }

  ngOnInit() {}

}
<div class="w-screen h-screen bg-gradient-primary">
    <swiper-container slides-per-view="1" loop="true" pagination="true" css-mode="true">
      <swiper-slide *ngFor="let data of slideData; let dataIndex=index;" class="w-screen h-screen flex flex-col">
        <div class="p-4"><img src="assets/icon/logo.svg"></div>
        <div class="w-full px-4 flex flex-col space-y-8">
          <p class="text-white text-4xl font-bold w-3/4">{{ data.header }}</p>
          <p class="text-white text-2xl">{{ data.synopsis }}</p>
       </div>
       <div class="w-full items-center justify-center">
        <img [src]="data.imageUrl" class="w-full object-cover">
      </div>
      <div class="w-full flex flex-col p-2 items-center justify-center space-y-3 absolute bottom-6">
        <p class="text-white text-2xl text-center font-light">{{ data.highlight }}
        <span class="w-full flex flex-col items-center justify-center pt-2 px-2">
          <ion-button color="none" class="border-brightOrange border-solid border-[1px] capitalize rounded-3xl px-4 bg-transparent text-brightOrange" routerLink="/auth/login">Skip</ion-button>
        </span>
        <span class="w-full flex items-center justify-between pt-2 px-2" *ngIf="">
          <ion-button color="none" class="border-brightOrange border-solid border-[1px] capitalize rounded-3xl px-4 bg-transparent text-brightOrange" routerLink="/auth/signup-start">Signup</ion-button>
          <ion-button color="none" class="border-brightOrange border-solid border-[1px] capitalize rounded-3xl px-4 bg-transparent text-brightOrange" routerLink="/auth/login">Login</ion-button>
        </span>
      </div>
      </swiper-slide>
    </swiper-container>
</div>
