import { CommonModule } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { IonicModule } from '@ionic/angular';

@Component({
  selector: 'app-book',
  templateUrl: './book.component.html',
  styleUrls: ['./book.component.scss'],
  standalone: true,
  imports: [IonicModule, CommonModule, FormsModule]
})
export class BookComponent  implements OnInit {

  constructor() { }

  ngOnInit() {}

}
<div class="w-full py-3">
    <div class="w-[90%] mx-auto flex flex-col space-y-6">
      <ion-input aria-label="whereTo" placeholder="Where are you headed?"  class="w-full mx-auto bg-transparent rounded-2xl placeholder:pl-2 border-solid border-2 border-brightOrange h-14 text-darkChocolate text-xl"/>
      <ion-select aria-label="Select Role" [(ngModel)]="selectedUser" class="rounded-2xl bg-transparent text-darkChocolate border-solid border-[1px] border-darkChocolate" placeholder="Select Role">
        <ion-select-option selected value="passenger">As Passenger</ion-select-option>
        <ion-select-option value="driver">As Driver</ion-select-option>
      </ion-select>
    </div>
</div>
