import { Component, OnInit, signal } from '@angular/core';
import { RouterLink } from '@angular/router';
import { IonicModule } from '@ionic/angular';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
  standalone: true,
  imports: [IonicModule, RouterLink]
})
export class LoginComponent  implements OnInit {

  passwordIsVisible = signal(false)

  constructor() { }

  ngOnInit() {}

  setPasswordVisibilityState(){
    this.passwordIsVisible.set(!this.passwordIsVisible())
  }

}
<div class="w-screen h-screen bg-gradient-primary relative">
  <img src="assets/icon/logo.svg" alt="" class="py-4 px-4">
  <form class="w-full py-2 px-2 absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 flex flex-col items-center justify-center space-y-4">
    <ion-text class="font-bold text-darkOrange text-3xl w-[90%] mx-auto text-center">Welcome back to Digitekisi</ion-text>
    <div class="relative w-[95%] mx-auto">
      <img src="assets/icon/email_icon.svg" class="absolute top-3 right-3"/>
      <input type="text" class="w-full bg-transparent border-solid border-2 border-darkChocolate h-12 focus:border-darkOrange focus:outline-none placeholder:text-darkChocolate px-2 rounded-xl text-xl" placeholder="Email*">
    </div>
    <div class="relative w-[95%] mx-auto">
      <img src="assets/icon/password_invisible.svg" class="absolute top-3 right-3" *ngIf="!passwordIsVisible()" (click)="setPasswordVisibilityState()"/>
      <img src="assets/icon/password_visible.svg" class="absolute top-3 right-3" (click)="setPasswordVisibilityState()"/>
      <input type="password" class="w-full bg-transparent border-solid border-2 border-darkChocolate h-12 focus:border-darkOrange focus:outline-none placeholder:text-darkChocolate px-2 rounded-xl text-xl" placeholder="Password*">
    </div>
    <button  class="capitalize text-2xl font-bold text-darkChocolate px-10 py-2 rounded-3xl bg-gradient-secondary">Login</button>
  </form>
  <ion-buttons class="absolute bottom-3 w-full flex items-center justify-between px-2">
    <ion-button class="capitalize" routerLink="/auth/password/forgot">Forgot password?</ion-button>
    <ion-button class="capitalize text-brightOrange" routerLink="/auth/password/reset">Create account</ion-button>
  </ion-buttons>
</div>
