import { CommonModule } from '@angular/common';
import { AfterViewInit, CUSTOM_ELEMENTS_SCHEMA, Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { GoogleMap } from '@capacitor/google-maps';
import { IonicModule } from '@ionic/angular';
import { Geolocation } from '@capacitor/'

@Component({
  selector: 'app-book',
  templateUrl: './book.component.html',
  styleUrls: ['./book.component.scss'],
  standalone: true,
  imports: [IonicModule, CommonModule, FormsModule],
  schemas: [CUSTOM_ELEMENTS_SCHEMA]
})
export class BookComponent  implements OnInit, AfterViewInit {

  @ViewChild('mapRef') mapRef!: ElementRef<HTMLElement>
  newMap!: GoogleMap
  activeStatus = 'active'

  constructor() { }

  ngOnInit() {}

  ngAfterViewInit(): void {
    this.initMap()
  }

  async initMap(){
    const { coords } = await Geolocation.getCurrentPosition()
    this.newMap = await GoogleMap.create({
      id: 'digitekisi-map',
      element: this.mapRef.nativeElement,
      apiKey: environment.GOOGLE_MAP_API_KEY,
      config: {
        center: {
          lat: coords.latitude,
          lng: coords.longitude,
        },
        zoom: 8,
      },
    });
  }

}
<div class="w-full py-3">
    <div class="w-[90%] mx-auto flex flex-col space-y-6">
      <ion-input aria-label="whereTo" placeholder="Where are you headed?"  class="w-full mx-auto bg-transparent rounded-2xl placeholder:pl-2 border-solid border-2 border-brightOrange h-14 text-darkChocolate text-xl"/>
      <ion-select aria-label="Select Driver"  class="rounded-2xl bg-transparent text-darkChocolate border-solid border-2 border-brightOrange" placeholder="Select Driver">
        <ion-select-option selected value="passenger">Tekisi Man 1</ion-select-option>
        <ion-select-option value="driver">TekisiMan 2</ion-select-option>
      </ion-select>
    </div>
    <div class="w-full p-2">
      <capacitor-google-map id="digitekisi-map" #mapRef></capacitor-google-map>
    </div>
</div>
