import { IonicModule } from '@ionic/angular';
import { Component } from "@angular/core";
import { HomeComponent } from './home/home.component';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'digitekisi-dashboard',
  standalone: true,
  templateUrl: './dashboard.component.html',
  imports: [IonicModule, HomeComponent, FormsModule]
})
export class DashboardComponent {
  selectedUser = 'As Passenger'
}
<div class="w-screen h-screen relative bg-white">
  <div class="w-full bg-gradient-primary h-20 flex items-center justify-between px-2">
    <img src="assets/icon/logo.svg"/>
    <span class="flex items-center justify-center space-x-2">
      <ion-select [(ngModel)]="selectedUser" class="rounded-2xl bg-transparent text-darkChocolate border-solid border-[1px] border-darkChocolate" placeholder="passenger">
        <ion-select-option selected value="passenger">As Passenger</ion-select-option>
        <ion-select-option value="driver">As Driver</ion-select-option>
      </ion-select>
      <img src="assets/icon/notifications.svg"/>
      <img src="assets/icon/logout.svg"/>
    </span>
  </div>
  <ion-router-outlet></ion-router-outlet>
</div>
