import { NgIf } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { FormsModule, ReactiveFormsModule, FormGroup, FormControl, Validators } from '@angular/forms';
import { RouterLink } from '@angular/router';
import { IonicModule } from '@ionic/angular';

@Component({
  selector: 'app-signup-end',
  templateUrl: './signup-end.component.html',
  styleUrls: ['./signup-end.component.scss'],
  standalone: true,
  imports: [RouterLink, IonicModule, FormsModule, ReactiveFormsModule, NgIf]
})
export class SignupEndComponent  implements OnInit {

  signupEndForm: FormGroup = new FormGroup({
    verificationCode: new FormControl('', [Validators.required, Validators.minLength(6), Validators.maxLength(6)]),
  })

  constructor() { }

  ngOnInit() {}

  endSignup(){}

}
<div class="w-screen h-screen bg-gradient-primary relative">
  <img src="assets/icon/logo.svg" alt="" class="py-4 px-4">
  <form class="w-full py-2 px-2 absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 flex flex-col items-center justify-center space-y-6" [formGroup]="signupEndForm">
    <ion-text class="font-bold text-darkOrange text-3xl w-[90%] mx-auto text-center">Complete Signup process</ion-text>
    <div class="relative w-[95%] mx-auto">
      <img src="assets/icon/verification_code.svg" class="absolute top-3 right-3"/>
      <input type="number" class="w-full bg-transparent border-solid border-2 border-darkChocolate h-14 focus:border-darkOrange focus:outline-none placeholder:text-darkChocolate px-2 rounded-xl text-xl" placeholder="Verification Code*" formControlName="verificationCode">
    </div>
    <button type="submit" class="capitalize text-2xl font-bold text-darkChocolate px-10 py-2 rounded-3xl bg-gradient-secondary flex items-center justify-center space-x-3 shadow-2xl" [disabled]="signupEndForm.invalid" (click)="endSignup()">
      <div class="hidden justify-center items-center">
        <div class="w-8 h-8 border-4 border-t-2 border-gray-200 rounded-full animate-spin" style="border-color: #00BFA5;"></div>
      </div>
      <div>Validate</div>
      </button>
  </form>
  <ion-buttons class="absolute bottom-3 w-full flex items-center justify-between px-2">
    <ion-button class="capitalize" routerLink="/auth/login">Go back to login</ion-button>
    <ion-button class="capitalize text-brightOrange" routerLink="/auth/signup/start">Create new account?</ion-button>
  </ion-buttons>
</div>
