import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-walkthrough',
  templateUrl: './walkthrough.component.html',
  styleUrls: ['./walkthrough.component.scss'],
  standalone: true,
})
export class WalkthroughComponent  implements OnInit {

  constructor() { }

  ngOnInit() {}

}
<div class="w-screen h-screen bg-gradient-primary">
  <img src="assets/icon/logo.svg" alt="" class="py-4 px-4">
  <div class="container mx-auto p-4 flex flex-col space-y-8">
    <p class="text-white text-4xl font-bold w-3/4">Taking you to the Cloud</p>
    <p class="text-white text-2xl">We have no change wahalas and want you to experience this joy too</p>
    <img src="assets/icon/landing.svg" alt="" class="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2">
    <!-- Bottom -->
    <div class="container mx-auto px-3 flex flex-col items-center justify-center space-y-3 absolute bottom-6">
      <p class="text-white text-2xl text-center">Secure your ride with the click of a button</p>
      <span>
        <ion-button></ion-button>
      </span>
    </div>
  </div>
</div>
